class InvDeposito extends BaseInvDeposito
{
  public function  __toString()
  {
      return sprintf('%s', $this->getNombre());
  }
  public function getTipoDeposito() {
    if($this->getTipo()==0) {
      return "DEPOSITO";
    } else if($this->getTipo()==1){
      return "PISO VENTA";
    } else if($this->getTipo()==2){
      return "VENCIDOS";
    }
  }
  public function getEmpresaName() {
    return $this["ename"];
  }
  public function getCompany() {
    return $this["emin"];
  }
  public function getCreatedAtTxt() {
    return format_datetime($this->getCreatedAt(), 'f', 'es_ES');
  }
  public function getUpdatedAtTxt() {
    return format_datetime($this->getUpdatedAt(), 'f', 'es_ES');
  }
}
